(function(e) {
    e(document).ready(function() {
        e(".block-top-link, #page-link a, #theme-link a, a.to-top, .theme_steps > a").click(function(t) {
            t.preventDefault();
            var n = this.href,
                r = n.split("#"),
                i = r[1],
                s = e("#" + i).offset(),
                o = s.top - 50;
            e("html, body").animate({
                scrollTop: o
            }, 500)
        });
        e("select.categories, .doc-group.select select").chosen();
        e(window).scroll(function() {
            e(this).scrollTop() > 200 ? e(".to-top").fadeIn() : e(".to-top").fadeOut()
        });
        jQuery("ul.tt-block li.doc:odd").addClass("odd");
        jQuery("ul.tt-block li.doc:even").addClass("even")
    });
    e(function() {
        e(".feedback mark, .feedback a").tipTip()
    })
})(jQuery);